import {useState } from 'react';
import React  from 'react'
import axios from 'axios';
import styled from "styled-components"

function App() {

  //const url='https://api.openweathermap.org/data/2.5/weather?q=lucknow&appid=80787932a4b56f17546ab68d18eb049f'

  return (
    <div className="App">
      <Container>
        <Top>
          <Location>
            <p>Lucknow</p>
          </Location>
          <Temp>
            <h1>60*F</h1>
          </Temp>
          <Description>
            <p>Clouds</p>
          </Description>
        </Top>
        <bottom>
          <feels>
            <p>65*F</p>
          </feels>
          <humidity>
            <p>20%</p>
          </humidity>
          <wind>
            12 MPH
          </wind>
        </bottom>
      </Container>
    </div>
  );
}

export default App;


const Container=styled.div`
  max-width:700px;
  height:700px;
  margin:auto;
  padding:0 1rem;
  position:relative;
  top:10%;
  display:flex;
  flex-direction:column;
  justify-content: space-between;


`
